DROP DATABASE IF EXISTS papyrus;CREATE DATABASE papyrus;USE papyrus;CREATE TABLE client(   cli_num INT AUTO_INCREMENT,   cli_nom VARCHAR(50)  NOT NULL,   cli_adresse VARCHAR(50)  NOT NULL,   cli_tel VARCHAR(30)  NOT NULL,   PRIMARY KEY(cli_num)   );CREATE TABLE commande(   com_num INT AUTO_INCREMENT,   com_date DATETIME NOT NULL,   com_obs VARCHAR(50)  NOT NULL,   cli_num INT NOT NULL,   PRIMARY KEY(com_num),   UNIQUE(cli_num),   FOREIGN KEY(cli_num) REFERENCES client(cli_num));CREATE TABLE produit(   pro_num INT AUTO_INCREMENT,   pro_libelle VARCHAR(50)  NOT NULL,   pro_description VARCHAR(50) ,   PRIMARY KEY(pro_num));CREATE TABLE est_compose(   com_num INT,   pro_num INT,   est_qte INT NOT NULL,   PRIMARY KEY(com_num, pro_num),   FOREIGN KEY(com_num) REFERENCES commande(com_num),   FOREIGN KEY(pro_num) REFERENCES produit(pro_num));CREATE INDEX `index_quelconque` ON `client` (`cli_num`);INSERT INTO `client` (`cli_nom`,`cli_adresse`, `cli_tel`)VALUES  ("margaux","Ap #650-9912 Id Ave",1234567890),  ("itezed","Ap #868-5660 Gravida Ave",987654321),  ("bidule","Ap #640-5388 Malesuada Rd.",741258963),  ("chouette","P.O. Box 240, 1274 A Road",963258441),  ("franklin","Ap #113-6002 Eu Av.",789652123),  ("aldrich","Ap #113-6002 Eu Av.",789654523),  ("lemoche","Ap #113-6002 Eu Av.",789612123);  INSERT INTO `commande` (`cli_num` ,`com_date`,`com_obs`)VALUES  (1,"2018-04-24 05:59:51","Ap #868-5660 Gravida Ave"),  (2,"2018-04-21 16:59:51","Ap #640-5388 Malesuada Rd."),  (3,"2018-04-20 17:59:51","P.O. Box 240, 1274 A Road"),  (4,"2018-04-26 19:59:51","Ap #113-6002 Eu Av."),  (5,"2018-04-22 11:59:51","Ap #113-6002 Eu Av."),  (6,"2018-04-13 13:59:51","Ap #113-6002 Eu Av.");  INSERT INTO `produit` (`pro_libelle`,`pro_description`)VALUES("épée","pratique pour planter itezed dans le ventre"),("rapière","pratique pour planter itezed au niveau du bras"),("espadon","pratique pour découper les membres de itezed"),("grand e